import React from "react";
import "./Product.css";
import { useStateValue } from "./StateProvider";
function Product({ id, title, image, price, rating }) {
  const [{ basket }, dispatch] = useStateValue();

  const addToBasket = () => {
    dispatch({
      type: "ADD_TO_BASKET",
      item: {
        id: id,
        title: title,
        image: image,
        price: price,
        rating: rating,
      },
    });
  };
  return (
    <div className="product">
      <div className="product_info">
        <p>{title}</p>
        <p className="product_price">
          <small>$</small>
          <strong>{price}</strong>
        </p>
        <div className="product__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>‚≠ê</p>
            ))}
        </div>
      </div>
      <img src={image} alt=""></img>
      <button className="addTOBasket" onClick={addToBasket}>
        Add to Basket
        <div class="col-sm-12">
          <div
            class="alert fade alert-simple alert-primary alert-dismissible text-left font__family-montserrat font__size-16 font__weight-light brk-library-rendered rendered show"
            role="alert"
            data-brk-library="component__alert"
          >
            <button
              type="button"
              class="close font__size-18"
              data-dismiss="alert"
            >
              <span aria-hidden="true">
                <i class="fa fa-times alertprimary"></i>
              </span>
              <span class="sr-only">Close</span>
            </button>
            <i class="start-icon fa fa-thumbs-up faa-bounce animated"></i>
            <strong class="font__weight-semibold">Well done!</strong> You
            successfullyread this important.
          </div>
        </div>
      </button>
    </div>
  );
}

export default Product;
