import React from 'react'
import "./Login.css"
import {Link, useHistory} from "react-router-dom"
import { useState } from 'react';
import {auth} from "./firebase";
function Login() {
    const history= useHistory();
    const [email, setEmail]=useState('');
    const [password, setpassword]=useState('');
    const signIn=e=>{
        e.preventDefault();
        auth
            .signInWithEmailAndPassword();
    }
    const register=e=>{
        e.preventDefault();
        auth
            .createUserWithEmailAndPassword(email,password)
            .then((auth)=>{
                console.log(auth);
                if(auth){
                    history.push('/')
                }
            })
            .catch(error=>alert(error.message))
    }

    return (
        <div className="login">
           <Link to="/"> <img className="login__logo" src="https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Amazon_logo.svg/1024px-Amazon_logo.svg.png"/></Link>
        
           <div className="login__container">
               <h1>Sign-in</h1>
               <form>
                   <h5>E-mail</h5> <input type="text" value={email} onChange={e=> setEmail(e.target.value)}/>
                   <h5>Password</h5> <input type="password" value={password} onChange={e=> setpassword(e.target.value)}/>
                   
                   <button type="submit" onClick={signIn} className="login__signInButton">Sign In</button>
               </form>
               <p>By continuing, you agree to AMAZON CLONE Conditions of Use and Privacy Notice.</p>
               <button onClick={register} className="login__registerButton">Create your Amazon account</button>
           </div>
        </div>
    )
}

export default Login
